import os

def read_words(directory):
    words = []
    for file_name in os.listdir(directory):
        if file_name.endswith(".txt"):
            with open(os.path.join(directory, file_name), "r") as file:
                words.append(file.read().split())
    return words

def top_5_words(words):
    counts = {}
    for word in words:
        if word in counts:
            counts[word] += 1
        else:
            counts[word] = 1
    top_5 = sorted(counts.items(), key=lambda x: x[1], reverse=True)[:5]
    return top_5

