#include <iostream>

int fibonacci(int n) {
  if (n == 0) {
    return 0;
  } else if (n == 1) {
    return 1;
  } else {
    return fibonacci(n - 1) + fibonacci(n - 2);
  }
}

int main() {
  int n;
  std::cout << "Enter the value of n: ";
  std::cin >> n;

  int fib_number = fibonacci(n);
  std::cout << "The " << n << "th Fibonacci number is: " << fib_number << std::endl;

  return 0;
}
```
